CREATE TABLE IF NOT EXISTS T_ITEM( F_ID BIGINT NOT NULL PRIMARY KEY AUTO_INCREMENT, F_NAME VARCHAR(40) NOT NULL, F_STATUS VARCHAR(20) NOT NULL, F_DELETED BOOLEAN NOT NULL) ENGINE InnoDB
CREATE TABLE IF NOT EXISTS T_AUDIT_ITEM( F_ID BIGINT NOT NULL PRIMARY KEY AUTO_INCREMENT, F_ACTION VARCHAR(10) NOT NULL, F_ITEM_ID BIGINT NOT NULL, F_DATE DATETIME NOT NULL, F_DELETED BOOLEAN NOT NULL, FOREIGN KEY (F_ITEM_ID) REFERENCES T_ITEM (F_ID)) ENGINE InnoDB